mapper.select:
select c.class_num, class_title, tutor_nickname, bcategory_num, bcategory_name,scategory_name, sloc_num, date_num, class_date from ( select s.bcategory_num, bcategory_name, scategory_num, scategory_name from small_category s, big_category b where s.bcategory_num=b.bcategory_num
)a, class_info c , class_date cd where a.scategory_num=c.scategory_num and cd.class_num=c.class_num and class_auth=2 and class_status=0
order by class_num desc limit #{startRow},6

select NVL(count(*),0) from ( select c.class_num, class_title, tutor_nickname, bcategory_num, bcategory_name,scategory_name, sloc_num, date_num, class_date from ( select s.bcategory_num, bcategory_name, scategory_num, scategory_name from small_category s, big_category b where s.bcategory_num=b.bcategory_num
)a, class_info c , class_date cd where a.scategory_num=c.scategory_num and cd.class_num=c.class_num and class_auth=2 and class_status=0
order by class_num desc )z
